INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (99643, 'Sofosbuvir', 'Modafinil', 'Major', 'Coadministration with inducers of P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of sofosbuvir and other direct-acting antiviral agents that may be given with sofosbuvir in fixed-dose combination products such as ledipasvir and velpatasvir. Induction of P-gp-mediated efflux in the intestine decreases the oral bioavailability of these antiviral agents, which are substrates of the transporter. The interaction has been studied with rifampin, a potent inducer of P-gp and CYP450 isoenzymes.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of P-gp inducers should generally be avoided during treatment with sofosbuvir, given either as a single-ingredient product or as a fixed-dose combination product with ledipasvir or velpatasvir.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of P-gp inducers should generally be avoided during treatment with sofosbuvir, given either as a single-ingredient product or as a fixed-dose combination product with ledipasvir or velpatasvir.', 'Absorption', 'Amphetamine, Pemoline, Atomoxetine, Metamfetamine, Lisdexamfetamine, Solriamfetol, Caffeine, Methylphenidate, Dexmethylphenidate, Dextroamphetamine, Armodafinil, Citicoline', 'Lamivudine, Paritaprevir, Cabotegravir, Simeprevir, Zanamivir, Velpatasvir, Oseltamivir, Brincidofovir, Stavudine, Remdesivir, Elbasvir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact