INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (99663, 'Ranolazine', 'Ledipasvir', 'Moderate', 'Coadministration with inhibitors of the P-glycoprotein (P-gp) efflux transporter may increase the oral bioavailability of ranolazine, which has been shown in vitro to be a substrate of P-gp. Ranolazine can prolong the QT interval in a dose-dependent manner, thus increased plasma levels may potentiate the risk of ventricular arrhythmias including ventricular fibrillation and torsade de pointes.', NULL, 'Caution is advised if ranolazine is prescribed in combination with P-gp inhibitors.', NULL, 'Caution is advised if ranolazine is prescribed in combination with P-gp inhibitors. Pharmacologic response to ranolazine should be monitored more closely whenever a P-gp inhibitor is added to or withdrawn from therapy, and the ranolazine dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Absorption', 'Alprostadil, Indomethacin, Ibuprofen, Ubidecarenone, Ivabradine, Adenosine, Regadenoson', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Acyclovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/99777/', '[1] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[2] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (99664, 'Sofosbuvir', 'Rifabutin', 'Major', 'Coadministration with potent inducers of P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of sofosbuvir and other direct-acting antiviral agents that may be given with sofosbuvir in fixed-dose combination products such as ledipasvir and velpatasvir. Induction of P-gp-mediated efflux in the intestine decreases the oral bioavailability of these antiviral agents, which are substrates of the transporter. The interaction has been studied with rifampin, a potent inducer of P-gp and CYP450 isoenzymes.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of potent P-gp inducers should generally be avoided during treatment with sofosbuvir, given either as a single-ingredient product or as a fixed-dose combination product with ledipasvir or velpatasvir.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of potent P-gp inducers should generally be avoided during treatment with sofosbuvir, given either as a single-ingredient product or as a fixed-dose combination product with ledipasvir or velpatasvir.', 'Absorption', 'Capreomycin, Pyrazinamide, Rifamycin, Bedaquiline, Sulfamethoxazole, Streptomycin, Trimethoprim, Cycloserine, Isoniazid, Ethambutol, Pyridoxine, More', 'Lamivudine, Paritaprevir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Remdesivir, Elbasvir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/99778/', '[1] "Product Information. Harvoni (ledipasvir-sofosbuvir)." Gilead Sciences, Foster City, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Epclusa (sofosbuvir-velpatasvir)." Gilead Sciences, Foster City, CA.[4] "Product Information. Sovaldi (sofosbuvir)." Gilead Sciences, Foster City, CA.[5] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (99665, 'Sofosbuvir', 'Rifampicin', 'Major', 'Coadministration with rifampin may significantly decrease the plasma concentrations of sofosbuvir and other direct-acting antiviral agents that may be given with sofosbuvir in fixed-dose combination products such as ledipasvir and velpatasvir. The proposed mechanism is induction of intestinal P-glycoprotein (P-gp)-mediated efflux by rifampin, which results in decreased oral bioavailability of the antiviral agents. Induction of CYP450 2B6, 2C8, and 3A4 may also contribute in the case of velpatasvir.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of rifampin should generally be avoided during treatment with sofosbuvir, given either as a single-ingredient product or as a fixed-dose combination product with ledipasvir or velpatasvir.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of rifampin should generally be avoided during treatment with sofosbuvir, given either as a single-ingredient product or as a fixed-dose combination product with ledipasvir or velpatasvir.', 'Metabolism, Absorption', 'Capreomycin, Pyrazinamide, Rifamycin, Bedaquiline, Sulfamethoxazole, Streptomycin, Trimethoprim, Cycloserine, Isoniazid, Ethambutol, Pyridoxine, More', 'Paritaprevir, Zanamivir, Velpatasvir, Elbasvir, Famciclovir, Tecovirimat, Pibrentasvir, Rimantadine, Valganciclovir, Telbivudine, Entecavir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/99779/', '[1] "Product Informa