INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (99675, 'Simeprevir', 'Ledipasvir', 'Major', 'Coadministration of ledipasvir and simeprevir may significantly increase the plasma concentrations of both drugs. The proposed mechanism is ledipasvir inhibition of the OATP1B1-mediated hepatic uptake of simeprevir and, conversely, simeprevir inhibition of the P-glycoprotein-mediated intestinal efflux of ledipasvir. In clinical trials, higher simeprevir exposures have been associated with increased frequency of adverse reactions including rash and photosensitivity.', NULL, 'Concomitant use of ledipasvir-sofosbuvir with simeprevir is not recommended.', NULL, 'Concomitant use of ledipasvir-sofosbuvir with simeprevir is not recommended.', 'Absorption', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/99789/', '[1] "Product Information. Harvoni (ledipasvir-sofosbuvir)." Gilead Sciences, Foster City, CA.[2] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (99676, 'Simvastatin', 'Ledipasvir', 'Moderate', 'Coadministration of HMG-CoA reductase inhibitors (statins) with HCV NS5A/NS5B inhibitors, such as ledipasvir, may significantly increase the plasma concentrations of the statin and the risk of adverse effects, such as myopathy and rhabdomyolysis. The proposed mechanism of this interaction has not been clearly delineated but may involve ledipasvir-mediated inhibition of the metabolism of statins via hepatic transport enzymes and/or CYP450 isoenzymes. This interaction has not been studied specifically with all statins; data are currently available for rosuvastatin.', NULL, 'Caution and close monitoring are recommended if ledipasvir is used concomitantly with a statin and a dosage reduction for the statin drug considered.', NULL, 'Caution and close monitoring are recommended if ledipasvir is used concomitantly with a statin and a dosage reduction for the statin drug considered. Patients should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, fever, malaise, and/or dark colored urine, particularly during the initial months of therapy and during any periods of upward dosage titration of either drug. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Guar gum, Gemfibrozil, Fenofibric acid, Mipomersen, Evinacumab, Evolocumab, Dextrothyroxine, Probucol, Cholestyramine, Ezetimibe, Clofibrate, More', 'Paritaprevir, Tenofovir alafenamide, Cabotegravir, Maraviroc, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Elbasvir, Doravirine, Bictegravir, More', 'Moderat