INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (99689, 'Tucatinib', 'Sofosbuvir', 'Moderate', 'Coadministration with tucatinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The mechanism likely involves tucatinib-mediated inhibition of P-gp. Increased exposure to the P-gp substrate may increase the risk of toxicity.', NULL, 'Caution is advised if tucatinib is used concomitantly with P-gp substrates, particularly those with a narrow therapeutic index.', NULL, 'Caution is advised if tucatinib is used concomitantly with P-gp substrates, particularly those with a narrow therapeutic index. A dose reduction of the P-gp substrates as well as clinical and laboratory monitoring may be appropriate.', 'Absorption', 'Lamivudine, Glecaprevir, Cabotegravir, Amprenavir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Velpatasvir, Dolutegravir, Oseltamivir, More', 'Entrectinib, Imatinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/99803/', '[1] "Product Information. Tukysa (tucatinib)." Seattle Genetics Inc, Bothell, WA.', 1767369485);
