INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (99951, 'Aminophylline', 'Levothyroxine', 'Moderate', 'The metabolism of theophylline is directly related to thyroid function. Theophylline clearance is decreased in hypothyroid patients and increased in hyperthyroid patients. Toxicity or subtherapeutic levels may occur. Similar interactions may occur with other methylxanthines.', NULL, 'When thyroid hormone dosage is instituted, discontinued, or changed, close observation for altered theophylline effect and serum concentrations is recommended.', NULL, 'When thyroid hormone dosage is instituted, discontinued, or changed, close observation for altered theophylline effect and serum concentrations is recommended. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms, or any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeats.', 'Others', 'Omalizumab, Tezepelumab, Benralizumab, Caffeine, Reslizumab, Dyphylline, Mepolizumab, Roflumilast, Bromotheophylline', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100065/', '[1] Bauman JH, Teichman S, Wible DA "Increased theophylline clearance in a patient with hyperthyroidism." Ann Allergy 52 (1984): 94-6[2] Vozeh S, Otten M, Staub JJ, Follath F "Influence of thyroid function on theophylline kinetics." Clin Pharmacol Ther 36 (1984): 634-40[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[5] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.', 1767369485);
