INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (49573, 'Bromocriptine', 'Lorlatinib', 'Moderate', 'Coadministration with lorlatinib may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The proposed mechanism is increased clearance due to lorlatinib-mediated induction of CYP450 3A4 metabolism.', NULL, 'Caution is advised when lorlatinib is used concomitantly with drugs that are substrates of CYP450 3A4.', NULL, 'Caution is advised when lorlatinib is used concomitantly with drugs that are substrates of CYP450 3A4. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lorlatinib is added to or withdrawn from therapy. Avoid concomitant use with lorlatinib where minimal decreases in concentration of the CYP450 3A4 substrate may lead to serious therapeutic failure. If coadministration is required, the CYP450 3A4 substrate dosage should be increased in accordance with approved product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/49644/', '[1] "Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (49574, 'Bromocriptine', 'Loxapine', 'Moderate', 'Agents with central antidopaminergic activity such as phenothiazines, neuroleptics, and certain antiemetics may antagonize the pharmacologic effects of dopaminergic drugs, and vice versa. In addition, the central nervous system depressant and hypotensive effects of these agents may be additively or synergistically increased when taken together.', NULL, 'Concomitant use of dopaminergic drugs with antidopaminergic agents should generally be avoided.', NULL, 'Concomitant use of dopaminergic drugs with antidopaminergic agents should generally be avoided. If coadministration is necessary, patients should be alerted to the possibility of excessive drowsiness and monitored for potentially diminished therapeutic response to both treatments. Patients treated for Parkinson''s disease should generally avoid antidopaminergic agents, particularly phenothiazines and older neuroleptic agents, since these agents may cause extrapyramidal reactions and exacerbate the symptoms of Parkinson''s disease. Likewise, patients with a major psychotic disorder should ordinarily not be treated with dopaminergic drugs because of the risk of exacerbating the psychosis with an increase in central dopaminergic tone.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/49645/', '[1] Weingarten JC, Thompson TL "The effect of thioridazine on prolactinoma growth in a schizophrenic man: case report." Gen Hosp Psychiatry 7 (1985): 364-6[2] "Product Information. Norprolac (quinagolide)." Ferring Inc, North York, IA.[3] "Product Information. Requip (ropinirole)." SmithKline Beecham,