INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (49967, 'Cyanocobalamin', 'Potassium chloride', 'Minor', 'Limited data suggest that orally administered potassium chloride may decrease the gastrointestinal absorption of vitamin B12. The mechanism is believed to be a reduction in ileal pH by potassium chloride, which significantly decreases the activity of intrinsic factor that is necessary for vitamin B12 absorption. However, the interaction should rarely lead to clinically significant deficiency because the body (primarily the liver) usually has a copious supply of vitamin B12 that can sustain a person for several years in the absence of adequate intake. As for the treatment of B12 deficiency-related anemia, non-oral routes of administration (e.g., parenteral, intranasal, sublingual) are generally preferred, which would bypass the potential interaction.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50038/', '[1] Palva IP, Salokannel SJ, Timonen T, Palva HL "Drug-induced malabsorption of vitamin B 12 . IV. Malabsorption and deficiency of B 12 during treatment with slow-release potassium chloride." Acta Med Scand 191 (1972): 355-7', 1767365204);
