INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50001, 'Coccidioides immitis spherule', 'Budesonide', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50072/', '[1] "Product Information. Spherulin (coccidioidin skin test)." ALK Laboratories Inc, Wallingford, CT.[2] "Product Information. Tuberculin PPD (Mantoux) (tuberculin purified protein derivative)." Connaught Laboratories Ltd, Toronto, ON.[3] "Product Information. Candin (candida albicans extract)." Nielsen Biosciences Inc, San Diego, CA.[4] "Product Information. MSTA Mumps Skin Test Antigen (mumps skin test antigen)." Aventis Pharmaceuticals, Swiftwater, PA.[5] "Product Information. Multitest CMI (skin test antigens, multiple)." Aventis Pharmaceuticals, Swiftwater, PA.[6] "Product Information. Histolyn-Cyl (histoplasmin)." ALK Laboratories Inc, Wallingford, CT.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50002, 'Colesevelam', 'Budesonide', 'Moderate', 'INTERVAL: Concurrent administration of bile acid sequestrants may delay and/or decrease the absorption of oral corticosteroids. Bile acid sequestrants can bind certain drugs in the intestine to form an insoluble complex that is excreted in the feces. Reduced bioavailability of hydrocortisone has been reported during concomitant administration with cholestyramine and colestipol. It is not known whether the interaction occurs with other bile acid binding resins such as colesevelam or with corticosteroids other than hydrocortisone, but the possibility should be considered.', NULL, 'Oral corticosteroids should be administered at least 1 hour before or 4 to 6 hours after a cholestyramine or colestipol dose, and at least 4 hours before a colesevelam dose.', NULL, 'Oral corticosteroids should be administered at least 1 hour before or 4 to 6 hours after a cholestyramine or colestipol dose, and at least 4 hours before a colesevelam dose.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50073/', '[1] Johansson C, Adamsson U, Stierner U, Lindsten T "Interaction by cholestyramine on the uptake of hydrocortisone in the gastrointestinal tract." Acta Med Scand 204 (1978): 509-12[2] "Product Information. Colestid (colestipol HCI)." Pharmacia and Upjohn, Kalamazoo, MI.[3] Nekl KE, Aron DC "Hydrocortisone-colestipol interaction." Ann Pharmacother 27 (1993): 980-1[4] "Product Information. Questran (cholestyramine)." Par Pharmaceutical Inc, Chestnut Ridge, NY.[5] "Product Information. Welchol (colesevelam)" Daiichi Sankyo, Inc., Parsippany, NJ.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50003, 'Colestipol', 'Budesonide', 'Moderate', 'INTERVAL: Concurrent administration of bile acid sequestrants may delay and/or decrease the absorption of oral corticosteroids. Bile acid sequestrants can bind certain drugs in the intestine to form an insoluble complex that is excreted in the feces. Reduced bioavailability of hydrocortisone has been reported during concomitant administration with cholestyramine and colestipol. It is not known whether the interaction occurs with other bile acid binding resins such as colesevelam or with corticosteroids other than hydrocortisone, but the possibility should be considered.', NULL, 'Oral corticosteroids should be administered at least 1 hour before or 4 to 6 hours after a cholestyramine or colestipol dose, and at least 4 hours before a colesevelam dose.', NULL, 'Oral corticosteroids should be administered at least 1 hour before or 4 to 6 hours after a cholestyramine or colestipol dose, and at least 4 hours before a colesevelam dose.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50074/', '[1] Johansson C, Adamsson U, Stierner U, Lindsten T "Interaction by cholestyramine on the uptake of hydrocortisone in the gastrointestinal tract." Acta Med Scand 204 (1978): 509-12[2] "Product Information. Colestid (colestipol HCI)." Pharmacia and Upjohn, Kalamazoo, MI.[3] Nekl KE, Aron DC "Hydrocortisone-colestipol interaction." Ann Pharmacother 27 (1993): 980-1[4] "Product Information. Questran (cholestyramine)." Par Pharmaceutical Inc, Chestnut Ridge, NY.[5] "Product Information. Welchol (colesevelam)" Daiichi Sankyo, Inc., Parsippany, NJ.', 1767365204)