INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50026, 'Doxazosin', 'Budesonide', 'Moderate', 'Corticosteroids may antagonize the effects of antihypertensive medications by inducing sodium and fluid retention. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) because they have greater mineralocorticoid activity. Conversely, some calcium channel blockers such as diltiazem and verapamil may increase corticosteroid plasma levels and effects by inhibiting their clearance via CYP450 3A4 metabolism.', NULL, 'Patients on prolonged (i.', NULL, 'Patients on prolonged (i.e., longer than about a week) or high-dose corticosteroid therapy should have blood pressure, electrolyte levels, and body weight monitored regularly, and be observed for the development of edema and congestive heart failure. The dosages of antihypertensive medications may require adjustment.', 'Metabolism, Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50097/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50027, 'Dronedarone', 'Budesonide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the systemic bioavailability of budesonide, which undergoes extensive first-pass and systemic metabolism via intestinal and hepatic CYP450 3A4. In pharmacokinetic studies, 6- to 8-fold increases in budesonide systemic exposure (AUC) have been observed during coadministration of the potent CYP450 3A4 inhibitor ketoconazole with different oral formulations of budesonide.', NULL, 'The possibility of increased systemic adverse effects of budesonide should be considered during coadministration with CYP450 3A4 inhibitors.', NULL, 'The possibility of increased systemic adverse effects of budesonide should be considered during coadministration with CYP450 3A4 inhibitors. If concomitant use cannot be avoided, the dosing times between budesonide and the CYP450 3A4 inhibitor should be separated by as much as possible. In addition, the lowest effective dosage of budesonide should be prescribed, and further adjustments made as necessary according to therapeutic response and tolerance.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50098/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Bolland MJ, Bagg W, Thomas MG, Lucas JA, Ticehurst R, Black PN "Cushing''s syndrome due to interaction between inhaled corticosteroids and itraconazole." Ann Pharmacother 38 (2004): 46-9[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Daveluy A, Raignoux C, Miremont-Salame G, et al. "Drug interactions between inhaled corticosteroids and enzymatic inhibitors." Eur J C