INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50180, 'Budesonide', 'Siponimod', 'Major', 'Coadministration of siponimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may increase the risk of unintended additive immunosuppressive effects.', NULL, 'The safety and efficacy of siponimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated.', NULL, 'The safety and efficacy of siponimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated. Caution is advised during coadministration and for 3 to 4 weeks after the last dose of siponimod. When switching from drugs with prolonged immune effects to siponimod, the half-life and mode of action of these drugs must be considered to avoid unintended additive immunosuppressive effects while at the same time minimizing risk of disease reactivation.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50251/', '[1] "Product Information. Mayzent (siponimod)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50181, 'Budesonide', 'Sirolimus', 'Moderate', 'Coadministration of sirolimus or tacrolimus with other drugs that are also metabolized by CYP450 3A4 may result in elevated plasma concentrations of the macrolide immunosuppressant and/or the coadministered drug(s). The mechanism is decreased drug clearance due to competitive inhibition of CYP450 3A4 activity.', NULL, 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', NULL, 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50252/', '[1] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[2] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[7] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50182, 'Budesonide', 'Smallpox (Vaccinia) Vaccine, Live', 'Major', 'The administration of live virus smallpox vaccine during corticosteroid therapy may be associated with a risk of localized or disseminated infection due to enhanced replication of vaccine virus in the presence of diminished immune competence. Patients receiving high dosages of systemic corticosteroids and those who are systemically immunosuppressed due to long-term topical or aerosol use of corticosteroids may also have an increased risk of neurologic reactions to vaccines and decreased or suboptimal immunologic response due to antibody inhibition. The risk of ocular complications may be increased in patients who are using ophthalmic corticosteroids.', NULL, 'Routine nonemergency smallpox vaccination is considered a relative contraindication in patients receiving immunosuppressive corticosteroid therapy or ophthalmic corticosteroids.', NULL, 'Routine nonemergency smallpox vaccination is considered a relative contraindication in patients receiving immunosuppressive corticosteroid therapy or ophthalmic corticosteroids. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored. The interval depends on the dosage, duration, and route of corticosteroid therapy administered, but may be at least 3 months in most cases of systemic corticosteroid use. In patients who have recently been vaccinated, high-dose corticosteroid therapy should not be initiated for at least 2 weeks. Household contacts of immunosuppressed patients should also not be vaccinated. However, there are no absolute contraindications to vaccination if a high-risk exposure has occurred. In an outbreak emergency, smallpox vaccine is generally recommended for all persons, regardless of medical conditions. The risk for experiencing serious complications from the vaccine should be weighed against the risk of acquiring a potentially fatal smallpox infection. Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50253/', '[1] CDC. Centers for Disease Control and Prevention "Clinical guidance for smallpox vaccine use in a postevent vaccination program. Available from: URL: http://www.cdc.gov/mmwr/preview/mmwrhtml/rr6402a1.htm." ([2015 Feb 20]):[2] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[3] "Product Information. Dryvax (smallpox vaccine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[4] CDC. Centers for Disease Control and Prevention "Smallpox vaccination clinic guide. Logistical considerations and guidance for state and local planning for emergency, large-scale, voluntary administration of smallpox vaccine in response to a smallpox outbreak. Available from: URL: http://www.bt.cdc.gov" ([2002 Oct 16]):', 1767365204);
