INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50663, 'Bumetanide', 'Mivacurium', 'Moderate', 'Loop diuretics may enhance or decrease the effect of nondepolarizing neuromuscular blockers. The mechanism is unknown.', NULL, 'The patient''s respiratory status and level of neuromuscular blockade should be closely monitored.', NULL, 'The patient''s respiratory status and level of neuromuscular blockade should be closely monitored. The dose of the neuromuscular blocker may require adjustment to achieve the desired effect.', 'Others', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Ciclopirox, Vemurafenib, More', 'Botulinum toxin type A', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50734/', '[1] Scappaticci KA, Ham JA, Sohn YJ, Miller RD, Dretchen KL "Effects of furosemide on the neuromuscular junction." Anesthesiology 57 (1982): 381-8', 1767369485);
