INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50766, 'Bumetanide', 'Tiludronic acid', 'Moderate', 'Coadministration of bisphosphonates and loop diuretics may potentiate the risk of hypocalcemia. Bisphosphonates alone often cause mild, asymptomatic hypocalcemia via inhibitive effects on bone resorption and possibly chelation of blood calcium. These effects may be additive with those of loop diuretics, which tend to increase the urinary excretion of calcium.', NULL, 'Caution is advised if bisphosphonates are coadministered with loop diuretics.', NULL, 'Caution is advised if bisphosphonates are coadministered with loop diuretics. Renal function as well as serum calcium levels should be monitored during and after treatment, since the effects of bisphosphonates can continue for many weeks after discontinuation. Patients and clinicians should be alert to signs and symptoms of clinical hypocalcemia such as muscle spasm, carpopedal spasm, laryngeal spasm, tetany, convulsions, mental changes (irritability, depression, psychosis), increased intracranial pressure, papilledema, QT interval prolongation, and cardiac arrhythmias.', 'Synergism', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, More', 'Romosozumab, Cholecalciferol, Denosumab, Burosumab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50837/', '[1] "Product Information. Zometa (zoledronic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Skelid (tilundronate)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50767, 'Bumetanide', 'Timolol', 'Moderate', 'Although they are often combined in clinical practice, diuretics and beta-blockers may increase the risk of hyperglycemia and hypertriglyceridemia in some patients, especially in patients with diabetes or latent diabetes. In addition, the risk of QT interval prolongation and arrhythmias (e.g. torsades de pointes) due to sotalol may be increased by potassium-depleting diuretics.', NULL, 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration.', NULL, 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration. Patients should be advised to seek medical assistance if they experience dizziness, weakness, fainting, fast or irregular heartbeats, or loss of blood glucose control.', 'Synergism', 'Methazolamide, Neostigmine, Pilocarpine, Physostigmine, Acetylcholine, Diclofenamide, Travoprost, Acetazolamide, Brinzolamide, Brimonidine, Guanethidine, Levobunolol', 'Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Ciclopirox, Darunavir, Ketoconazole, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50838/', '[1] Dean S, Kendall MJ, Potter S, Thompson MH, Jackson DA "Nadolol in combination with indapamide and xipamide in resistant hypertensives." Eur J Clin Pharmacol 28 (1985): 29-33[2] Dornhorst A, Powell SH, Pensky J "Aggravation by propranolol of hyperglycaemic effect of hydrochlorothiazide in type II diabetics without alteration of insulin secretion." Lancet 1 (1985): 123-6[3] "Product Information. Lozol (