INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50792, 'Bumetanide', 'Ziprasidone', 'Major', 'Ziprasidone can cause dose-related prolongation of the QT interval. While clinical data are lacking, the coadministration of ziprasidone and agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential.', NULL, 'Caution is advised when ziprasidone must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, 'Caution is advised when ziprasidone must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with ziprasidone. Patients should be advised to notify their physician if they experience possible signs of an electrolyte imbalance, such as weakness, lethargy, drowsiness, confusion, muscle pains or cramps, dizziness, nausea, vomiting, tachycardia, or an irregular heartbeat.', 'Synergism', 'Pimavanserin', 'Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Clindamycin, Ciclopirox, Probenecid, Tioconazole, Brinzolamide, Furazolidone, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50863/', '[1] "Product Information. Geodon (ziprasidone)." Pfizer US Pharmaceuticals, New York, NY.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50793, 'Bumetanide', 'Zoledronic acid', 'Moderate', 'Coadministration of bisphosphonates and loop diuretics may potentiate the risk of hypocalcemia. Bisphosphonates alone often cause mild, asymptomatic hypocalcemia via inhibitive effects on bone resorption and possibly chelation of blood calcium. These effects may be additive with those of loop diuretics, which tend to increase the urinary excretion of calcium.', NULL, 'Caution is advised if bisphosphonates are coadministered with loop diuretics.', NULL, 'Caution is advised if bisphosphonates are coadministered with loop diuretics. Renal function as well as serum calcium levels should be monitored during and after treatment, since the effects of bisphosphonates can continue for many weeks after discontinuation. Patients and clinicians should be alert to signs and symptoms of clinical hypocalcemia such as muscle spasm, carpopedal spasm, laryngeal spasm, tetany, convulsions, mental changes (irritability, depression, psychosis), increased intracranial pressure, papilledema, QT interval prolongation, and cardiac arrhythmias.', 'Synergism', 'Romosozumab, Cholecalciferol, Denosumab, Burosumab', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Trichlormethiazide, Ciclopirox, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50864/', '[1] "Product Information. Zometa (zoledronic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Skelid (tilundronate)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 1767369485);
