INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50870, 'Buprenorphine', 'Berotralstat', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of buprenorphine, which is partially metabolized (approximately 30%) by the isoenzyme.', NULL, 'Caution is advised if buprenorphine is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if buprenorphine is prescribed with CYP450 3A4 inhibitors. Induction with buprenorphine should begin at a reduced dosage, and dosage escalation should occur more slowly to allow for assessment of opiate effects and development of patient tolerance. In patients who are already stabilized on buprenorphine, pharmacologic response and vital signs should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the buprenorphine dosage adjusted as necessary.', 'Metabolism', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Oxymorphone, Tapentadol, More', 'Hemin, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Streptokinase, Human C1-esterase inhibitor, Crizanlizumab, Lanadelumab, Icatibant', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50941/', '[1] "Product Information. Buprenex (buprenorphine)." Reckitt and Colman Pharmaceutical, Richmond, VA.', 1767369485);
