INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51036, 'Idelalisib', 'Buprenorphine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of buprenorphine, which is partially metabolized (approximately 30%) by the isoenzyme.', NULL, 'Caution is advised if buprenorphine is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if buprenorphine is prescribed with CYP450 3A4 inhibitors. Induction with buprenorphine should begin at a reduced dosage, and dosage escalation should occur more slowly to allow for assessment of opiate effects and development of patient tolerance. In patients who are already stabilized on buprenorphine, pharmacologic response and vital signs should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the buprenorphine dosage adjusted as necessary.', 'Metabolism', 'Diamorphine, Acamprosate, Lofexidine, Varenicline, Nalmefene, Nicotine, Morphine, Oxymorphone, Tapentadol, Meperidine, Hydromorphone, More', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Zanubrutinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51107/', '[1] "Product Information. Buprenex (buprenorphine)." Reckitt and Colman Pharmaceutical, Richmond, VA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51037, 'Ifosfamide', 'Buprenorphine', 'Moderate', 'Centrally-acting agents such as antiemetics, sedatives, narcotics, or antihistamines may add to the neurotoxic effects of ifosfamide. Administration of ifosfamide can cause CNS toxicity, which necessitates careful monitoring of the patient. Neurologic manifestations include somnolence, confusion, hallucinations, blurred vision, psychotic behavior, extrapyramidal symptoms, urinary incontinence, seizures, and in some instances, coma. There have also been reports of peripheral neuropathy. Ifosfamide neurotoxicity may occur within a few hours to a few days after initial administration and typically resolves within 48 to 72 hours of ifosfamide discontinuation.', NULL, 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide.', NULL, 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide. If ifosfamide-induced encephalopathy occurs, both ifosfamide and non-essential concomitant CNS agents should be discontinued. Supportive therapy should be initiated and maintained until complete resolution of neurotoxicity.', 'Synergism', 'Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Acetylsalicylic acid, Naloxone, Naltrexone', 'Temozolomide, Thiotepa, Carmustine, Cyclophosphamide, Busulfan, Streptozocin, Chlorambucil, Lomustine, Dacarbazine, Mechlorethamine, Bendamustine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51108/', '[1] Simonian NA, Gilliam FG, Chiappa KH "Ifosfamide causes a diazepam-sensitive encephalopathy." Neurology 43 (1993): 2700-2[2] "Product Information. Ifex (ifosfamide)." Bristol-Myers Squibb, Princeton, NJ.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
