INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51129, 'Buprenorphine', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of buprenorphine, which is partially metabolized (approximately 30%) by the isoenzyme.', NULL, 'Caution is advised if buprenorphine is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if buprenorphine is prescribed with CYP450 3A4 inhibitors. Induction with buprenorphine should begin at a reduced dosage, and dosage escalation should occur more slowly to allow for assessment of opiate effects and development of patient tolerance. In patients who are already stabilized on buprenorphine, pharmacologic response and vital signs should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the buprenorphine dosage adjusted as necessary.', 'Metabolism', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Codeine, Naltrexone, Oxymorphone, More', 'Rifamycin, Kanamycin, Polymyxin B, Streptomycin, Fidaxomicin, Rifaximin, Paromomycin, Neomycin, Micafungin, Caspofungin, Oteseconazole, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51200/', '[1] "Product Information. Buprenex (buprenorphine)." Reckitt and Colman Pharmaceutical, Richmond, VA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51130, 'Buprenorphine', 'Midazolam', 'Major', 'Concomitant use of buprenorphine with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may increase the risk of buprenorphine overdose, severe respiratory depression, coma, and death.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery until they know how these medications affect them. Extreme caution is advised when prescribing buprenorphine to patients who are addicted to opioids and also abusing benzodiazepines or alcohol. Due to potential risk of overdose and death, dependence on sedative-hypnotics such as benzodiazepines or alcohol is considered a relative contraindication for office-based buprenorphine treatment of opioid addiction.', 'Synergism', 'Zopiclone', 'Acamprosate, Naltrexone, Varenicline, Nalmefene, Nicotine, Naloxone, Naltrexone, Celecoxib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51201/', '[1] Sekar M, M