INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51209, 'Buprenorphine', 'Phenytoin', 'Major', 'Concomitant use of buprenorphine with other central nervous system (CNS) depressants has been associated with increased risk of buprenorphine overdose, severe respiratory depression, coma, and death. Coadministration with potent inducers of CYP450 3A4, including certain anticonvulsants such as carbamazepine, phenobarbital, phenytoin and primidone, may decrease the plasma concentrations of buprenorphine, which is primarily metabolized by the isoenzyme. Reduced efficacy or withdrawal symptoms may occur in patients maintained on buprenorphine.', NULL, 'Extreme caution is advised when prescribing buprenorphine in conjunction with other CNS depressants, or when prescribing buprenorphine to patients who are addicted to opioids and also abusing benzodiazepines or alcohol.', NULL, 'Extreme caution is advised when prescribing buprenorphine in conjunction with other CNS depressants, or when prescribing buprenorphine to patients who are addicted to opioids and also abusing benzodiazepines or alcohol. Due to potential risk of overdose and death, dependence on sedative-hypnotics such as benzodiazepines or alcohol is considered a relative contraindication for office-based buprenorphine treatment of opioid addiction. Dosage reduction of CNS depressants, buprenorphine, or both should be considered if concomitant use is necessary. Pharmacologic response to buprenorphine should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the buprenorphine dosage adjusted as necessary.', 'Metabolism, Synergism', 'Nalmefene, Acamprosate, Naloxone', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51280/', '[1] "Product Information. Suboxone (buprenorphine-naloxone)." Reckitt and Colman Pharmaceuticals Inc, Richmond, VA.[2] Substance Abuse and Mental Health Services Administration (US) "Clinical Guidelines for the Use of Buprenorphine in the Treatment of Opioid Addiction. Treatment Improvement Protocol (TIP) Series, No. 40 Available from: URL: http://www.ncbi.nlm.nih.gov/books/NBK64245/" ([2004]):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51210, 'Buprenorphine', 'Pimavanserin', 'Major', 'Concomitant use of buprenorphine with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may increase the risk of buprenorphine overdose, severe respiratory depression, coma, and death.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery until they know how these medications affect them. Extreme caution is advised when prescribing buprenorphine to patients who are addicted to opioids and also abusing benzodiazepines or alcohol. Due to potential risk of overdose and death, dependence on sedative-hypnotics such as benzodiazepines or alcohol is considered a relative contraindication for office-based buprenorphine treatment of opioid addiction.', 'Synergism', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Acetaminophen, Opium, Ibuprofen, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/s