INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51259, 'Buprenorphine', 'Ritonavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of buprenorphine, which is partially metabolized (approximately 30%) by the isoenzyme.', NULL, 'Caution is advised if buprenorphine is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if buprenorphine is prescribed with CYP450 3A4 inhibitors. Induction with buprenorphine should begin at a reduced dosage, and dosage escalation should occur more slowly to allow for assessment of opiate effects and development of patient tolerance. In patients who are already stabilized on buprenorphine, pharmacologic response and vital signs should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the buprenorphine dosage adjusted as necessary.', 'Metabolism', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, More', 'Diamorphine, Acamprosate, Lofexidine, Nalmefene, Nicotine, Oxymorphone, Tapentadol, Dezocine, Opium, Pentazocine, Dihydrocodeine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51330/', '[1] "Product Information. Buprenex (buprenorphine)." Reckitt and Colman Pharmaceutical, Richmond, VA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51260, 'Buprenorphine', 'Rivastigmine', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Codeine, Naltrexone, Oxymorphone, Tapentadol, Meperidine, More', 'Memantine, Aducanumab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51331/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51261, 'Buprenorphine', 'Rizatriptan', 'Moderate', 'Opioids may potentiate the effects of serotonergic agents and increase the risk of serotonin syndrome. Serotonin syndrome is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea. Since many serotonergic agents can also cause central nervous system depression, concomitant use with opioids may result in increased sedation and impairment of judgment, thinking, and psychomotor skills.', NULL, 'Caution is advised when opioids are used concomitantly with serotonergic agents such as selective serotonin reuptake inhibitors (SSRIs), serotonin and norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), other antidepressants/psychotropic agents (e.', NULL, 'Caution is advised when opioids are used concomitantly with serotonergic agents such as selective serotonin reuptake inhibitors (SSRIs), serotonin and norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), other antidepressants/psychotropic agents (e.g., amoxapine, buspirone, lithium, maprotiline, mirtazepine, nefazodone, trazodone, vilazodone), 5-HT1 receptor agonists (triptans), 5-HT3 receptor antagonists, cyclobenzaprine, dextromethorphan, 5-hydroxytryptophan, and St. John''s wort. Patients should be monitored for symptoms of the serotonin syndrome during treatment. If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary.', 'Synergism', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Disulfiram, Nalmefene, Nicotine, Opium, Dextropropoxyphene, Naloxone, Naltrexone', 'Rimegepant, Eptinezumab, Dihydroergotamine, Ubrogepant, Ergotamine, Galcanezumab, Atogepant, Methysergide, Erenumab, Fremanezumab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51332/', '[1] Vizcaychipi MP, Walker S, Palazzo M "Serotonin syndrome triggered by tramadol." Br J Anaesth 99 (2007): 919[2] Das PK, Warkentin DI, Hewko R, Forrest DL "Serotonin syndrome after concomitant treatment with linezolid and meperidine." Clin Infect Dis 46 (2008): 264-5[3] Mittino D, Mula M, Monaco F "Serotonin syndrome associated with tramadol-sertraline coadministration." Clin Neuropharmacol 27 (2004): 150-1[4] Gillman PK "Possible serotonin syndrome with moclobemide and pethidine." Med J Aust 162 (1995): 554[5] Mason BJ, Blackburn KH "Possible serotonin syndrome associated with tramadol and sertraline coadministration." Ann Pharmacother 31 (1997): 175-7[6] Dougherty JA, Young H, Shafi T "Serotonin syndrome induced by amitriptyline, meperidine, and venlafaxine." Ann Pharmacother 36 (2002): 1647-1648[7] Kesavan S, Sobala GM "Serotonin syndrome with fluoxetine plus tramadol." J R Soc Med 92 (1999): 474-5[8] Insler SR, Kraenzler EJ, Licina MG, Savage RM, Starr NJ "Cardiac surgery in a patient taking monoamine oxidase inhibitors - an adverse fentanyl reaction." Anesth Analg 78 (1994): 593-7[9] Hansen TE, Dieter K, Keepers GA "Interaction of fluoxetine and pentazocine." Am