INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51415, 'Cenobamate', 'Buspirone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of buspirone, which is primarily metabolized by the isoenzyme. Conversely, discontinuation of an inducer may increase buspirone plasma concentrations and increase the risk of central nervous system depression and other adverse effects.', NULL, 'The potential for diminished pharmacologic effects of buspirone should be considered during coadministration with CYP450 3A4 inducers.', NULL, 'The potential for diminished pharmacologic effects of buspirone should be considered during coadministration with CYP450 3A4 inducers. Pharmacologic response to buspirone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the buspirone dosage adjusted as necessary.', 'Metabolism', 'Lacosamide', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51486/', '[1] "Product Information. Buspar (buspirone)." Bristol-Myers Squibb, Princeton, NJ.[2] Kivisto KT, Lamberg TS, Neuvonen PJ "Interactions of buspirone with itraconazole and rifampicin: Effects on the pharmacokinetics of the active 1-(2-pyrimidinyl)-piperazine metabolite of buspirone." Pharmacol Toxicol 84 (1999): 94-7[3] Lamberg TS, Kivisto KT, Neuvonen PJ "Concentrations and effects of buspirone are considerably reduced by rifampicin." Br J Clin Pharmacol 45 (1998): 381-5[4] Lilja JJ, Kivisto KT, Backman JT, Lamberg TS, Neuvonen PJ "Grapefruit juice substantially increases plasma concentrations of buspirone." Clin Pharmacol Ther 64 (1998): 655-60[5] "Product Information