INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51481, 'Esketamine', 'Buspirone', 'Moderate', 'Concomitant use of esketamine with central nervous system (CNS) depressants may increase sedation and impairment of attention, judgment, thinking, reaction speed, and psychomotor skills.', NULL, 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants.', NULL, 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants. Due to the risk of delayed or prolonged sedation and other adverse effects, patients should be monitored for at least 2 hours after esketamine administration, followed by an assessment to determine when the patient is considered clinically stable and ready to leave the healthcare setting. Patients should be instructed not to engage in potentially hazardous activities, such as driving a motor vehicle or operating machinery, until the next day after a restful sleep.', 'Synergism', 'Halothane, Thiopental, Isoflurane, Nitrous oxide, Desflurane, Enflurane, Sevoflurane, Etomidate', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51552/', '[1] "Product Information. Spravato (esketamine)." Janssen Pharmaceuticals, Titusville, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51482, 'Eslicarbazepine', 'Buspirone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of buspirone, which is primarily metabolized by the isoenzyme. Conversely, discontinuation of an inducer may increase buspirone plasma concentrations and increase the risk of central nervous system depression and other adverse effects.', NULL, 'The potential for diminished pharmacologic effects of buspirone should be considered during coadministration with CYP450 3A4 inducers.', NULL, 'The potential for diminished pharmacologic effects of buspirone should be considered during coadministration with CYP450 3A4 inducers. Pharmacologic response to buspirone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the buspirone dosage adjusted as necessary.', 'Metabolism', 'Lacosamide', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51553/', '[1] "Product Information. Buspar (buspirone)." Bristol-Myers Squibb, Princeton, NJ.[2] Kivisto KT, Lamberg TS, Neuvonen PJ "Interactions of buspirone with itraconazole and rifampicin: Effects on the pharmacokinetics of the active 1-(2-pyrimidinyl)-piperazine metabolite of buspirone." Pharmacol Toxicol 84 (1999): 94-7[3] Lamberg TS, Kivisto KT, Neuvonen PJ "Concentrations and effects of buspirone are considerably reduced by rifampicin." Br J Clin Pharmacol 45 (1998): 381-5[4] Lilja JJ, Kivisto KT, Backman JT, Lamberg TS, Neuvonen PJ "Grapefruit juice substantially increases plasma concentrations of buspirone." Clin Pharmacol Ther 64 (1998): 655-60[5] "Product Information. Buspar (buspirone)." Bristol-Myers Squibb, Princeton, NJ.[6] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51483, 'Esmolol', 'Buspirone', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51554/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991): 390-1[4] Pacher P, Kecskemeti V "Cardiovascular side effects of new antidepressants and antipsychotics: new drugs, old concerns?" Curr Pharm Des 10 (2004): 2463-75[5] Cerner Multum, Inc. "Australian Product Information." O