INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51498, 'Fluconazole', 'Buspirone', 'Moderate', 'The combination of buspirone with azole antifungals may lead to significantly elevated buspirone plasma concentrations. The probable mechanism is competitive inhibition of CYP450 3A4 first-pass metabolism by the antifungal.', NULL, 'Close clinical observation is recommended if patients receiving buspirone are started on, stopped or receive a dose change of an azole antifungal.', NULL, 'Close clinical observation is recommended if patients receiving buspirone are started on, stopped or receive a dose change of an azole antifungal. If buspirone is started in a patient currently receiving an azole antifungal, the clinician may wish to initiate buspirone doses conservatively. Patients should be cautioned about increased CNS side effects including dizziness, drowsiness, and impaired psychomotor performance.', 'Metabolism', 'Tinidazole, Tetracycline, Norfloxacin, Cefuroxime, Secnidazole, Cefixime, Amikacin, Levofloxacin, Caspofungin, Micafungin, Amphotericin B, More', 'Halazepam, Clorazepic acid, Chlordiazepoxide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51569/', '[1] Kivisto KT, Lamberg TS, Neuvonen PJ "Interactions of buspirone with itraconazole and rifampicin: Effects on the pharmacokinetics of the active 1-(2-pyrimidinyl)-piperazine metabolite of buspirone." Pharmacol Toxicol 84 (1999): 94-7[2] Kivisto KT, Lamberg TS, Kantola T, Neuvonen PJ "Plasma buspirone concentrations are greatly increased by erythromycin and itraconazole." Clin Pharmacol Ther 62 (1997): 348-54[3] Lilja JJ, Kivisto KT, Backman JT, Lamberg TS, Neuvonen PJ "Grapefruit juice substantially i