        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1354655, 'Triamcinolone', 'Mephenytoin', 'Moderate', 'Phenytoin and other hydantoins may induce the CYP450 3A4 hepatic metabolism of corticosteroids and increase their clearance and decrease their half-lives, possibly reducing their therapeutic efficacy.', NULL, 'Patients should be closely monitored for clinical and laboratory evidence of reduced corticosteroid effects and changes in phenytoin concentrations during concomitant therapy.', NULL, 'Patients should be closely monitored for clinical and laboratory evidence of reduced corticosteroid effects and changes in phenytoin concentrations during concomitant therapy. Some patients may require increased corticosteroid dosages.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
