INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51521, 'Ioflupane I-123', 'Buspirone', 'Moderate', 'Drugs that bind to the dopamine transporter with high affinity may interfere with the image obtained using ioflupane I-123, which also binds to the dopamine transporter. These drugs include amoxapine, amphetamine, benztropine, bupropion, buspirone, cocaine, diethylpropion, ketamine, mazindol, methamphetamine, methylphenidate, modafinil, pemoline, phencyclidine (PCP), phentermine, phenylpropanolamine, pipradrol, selegiline, sertraline, and trihexyphenidyl. Selective serotonin reuptake inhibitors (SSRIs) and serotonin-norepinephrine reuptake inhibitors (SNRIs) may increase or decrease ioflupane binding to the dopamine transporter. Whether discontinuation of these drugs prior to ioflupane I-123 administration may help to minimize potential interference is unknown.', NULL, 'Clinicians using ioflupane I-123 should be aware of possible diagnostic interference by drugs that can compete for binding to the dopamine transporter.', NULL, 'Clinicians using ioflupane I-123 should be aware of possible diagnostic interference by drugs that can compete for binding to the dopamine transporter.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51592/', '[1] "Product Information. DaTscan (ioflupane I-123)." GE Healthcare, Princeton, NJ.[2] "Product Information. Viibryd (vilazodone)." Trovis Pharmaceuticals LLC, New Haven, CT.', 1767365204);
