INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51929, 'Cariprazine', 'Butalbital', 'Moderate', 'Coadministration with inducers of CYP450 3A4 is expected to alter the pharmacokinetics of cariprazine and its major active metabolites. Cariprazine is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2D6 to desmethyl cariprazine (DCAR) and didesmethyl cariprazine (DDCAR). DCAR is further metabolized to DDCAR by CYP450 3A4 and 2D6, and DDCAR is then metabolized by CYP450 3A4 to a hydroxylated metabolite. Both DCAR and DDCAR have in vitro receptor binding profiles similar to the parent drug and are considered pharmacologically equipotent to cariprazine.', NULL, 'Concomitant use of cariprazine with CYP450 3A4 inducers has not been evaluated and is not recommended.', NULL, 'Concomitant use of cariprazine with CYP450 3A4 inducers has not been evaluated and is not recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/52000/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Vraylar (cariprazine)." Actavis Pharma, Inc., Parsippany, NJ.[3] "Product Information. Vraylar (cariprazine)." Actavis Pharma, Inc., Parsippany, NJ.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (51930, 'Carvedilol', 'Butalbital', 'Moderate', 'Coadministration with barbiturates may decrease the plasma concentrations and pharmacologic effects of certain beta-blockers when administered orally. The proposed mechanism is barbiturate induction of hepatic microsomal and first-pass metabolism.', NULL, 'Barbiturates may variously reduce the effects of certain beta-blockers when given for more than a few days.', NULL, 'Barbiturates may variously reduce the effects of certain beta-blockers when given for more than a few days. Pharmacologic response to beta-blockers should be monitored more closely whenever a barbiturate is added to or withdrawn from therapy, and the beta-blocker dosage adjusted as necessary. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/52001/', '[1] Mantyla R, Mannisto P, Nykanen S, Koponen A, Lamminsivu U "Pharmacokinetic interactions of timolol with vasodilating drugs, food and phenobarbitone in healthy human volunteers." Eur J Clin Pharmacol 24 (1983): 227-30[2] Seideman P, Borg K, Haglund K, Von Bahr C, "Decreased plasma concentrations and clinical effects of alprenolol during combined treatment with pentobarbitone in hypertension." Br J Clin Pharmacol 23 (1987): 267-71[3] Haglund K, Seideman P, Colliste P, Borg KO, von Bahr C "Influence of pentobarbital on metoprolol plasma levels." Clin Pharmacol Ther 26 (1979): 326-9[4] Sotaniemi EA, Anttila M, Pelkonen RO, Jarvensivu P, Sundquist H "Plasma clearance of propranolol and sotalol and hepatic drug-metaboli