INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1354900, 'Dasatinib', 'Armodafinil', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of dasatinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of dasatinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected, or a dosage increase for dasatinib depending on patient tolerability. Close monitoring for toxicities (e.g., myelosuppression, bleeding complications, fluid retention, QT prolongation) is recommended if the dosage of dasatinib is increased. The dosage should be reduced to the indicated dosage following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'The potential for diminished pharmacologic effects of dasatinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1354901, 'Deferasirox', 'Armodafinil', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Pharmacologic response to modafinil or armodafinil should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy. Dose adjustments may be required if an interaction is suspected.', 'Metabolism', 'Pharmacologic response to modafinil or armodafinil should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1354902, 'Delavirdine', 'Armodafinil', 'Moderate', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may decrease the plasma concentrations of delavirdine due to induction of CYP450 3A4 metabolism. Conversely, delavirdine may increase the plasma concentrations of modafinil due to inhibition of CYP450 3A4. The clinical significance is unknown. Modafinil and armodafinil are modest CYP450 3A4 inducers in vitro, while delavirdine is considered a potent inhibitor of the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with decreased or subtherapeutic antiretroviral drug levels, caution is advised if modafinil or armodafinil must be used in patients treated with delavirdine. Clinical monitoring for altered effects of both drugs may be appropriate following addition or withdrawal of one or the other drug.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with decreased or subtherapeutic antiretroviral drug levels, caution is advised if modafinil or armodafinil must be used in patients treated with delavirdine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1354903, 'Desipramine', 'Armodafinil', 'Moderate', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may increase the plasma concentrations of tricyclic antidepressants (TCAs) in patients who are deficient in CYP450 2D6 (approximately 7% to 10% of the Caucasian population; similar or lower in other populations). Modafinil and armodafinil are modest inhibitors of CYP450 2C19, an isoenzyme that provides an ancillary pathway for the metabolism of certain TCAs normally metabolized by CYP450 2D6 such as clomipramine or desipramine.', 'DDInter', 1767317475, 'Caution is advised if tricyclic antidepressants are coadministered with modafinil or armodafinil in CYP450 2D6-deficient patients. Pharmacologic effects and serum TCA levels should be monitored more closely whenever modafinil or armodafinil is added to or withdrawn from therapy, and the TCA dosage adjusted accordingly. Patients should be advised to notify their physician if they experience possible signs of TCA toxicity such as excessive drowsiness, restlessness, agitation, ataxia, blurred vision, tachycardia, arrhythmia, and seizures.', 'Metabolism', 'Caution is advised if tricyclic antidepressants are coadministered with modafinil or armodafinil in CYP450 2D6-deficient patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
