INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1354905, 'Dexmethylphenidate', 'Armodafinil', 'Minor', 'In single-dose pharmacokinetic studies, healthy volunteers given modafinil (200 mg) with dextroamphetamine (10 mg) or methylphenidate (40 mg) exhibited a delay in the absorption of modafinil by approximately 1 hour. The mechanism of interaction is unknown. No other alterations in the pharmacokinetics of any of the drugs were observed. Although not studied, the interaction is likely to also occur with the various prodrug, racemic, and enantiomeric formulations of these drugs (e.g., amphetamine, lisdexamfetamine, dexmethylphenidate, armodafinil).', 'DDInter', 1767317475, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
