INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1354907, 'Diazepam', 'Armodafinil', 'Moderate', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 2C19 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2C19 activity by modafinil and armodafinil.', 'DDInter', 1767317475, 'Caution is advised if modafinil or armodafinil must be used concurrently with medications that undergo metabolism by CYP450 2C19, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever modafinil or armodafinil is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if modafinil or armodafinil must be used concurrently with medications that undergo metabolism by CYP450 2C19, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
