INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1354934, 'Enzalutamide', 'Armodafinil', 'Moderate', 'Coadministration with inducers of CYP450 2C8 and/or 3A4 may decrease the plasma concentrations of enzalutamide, which is primarily metabolized by CYP450 2C8 to its pharmacologically active metabolite, N-desmethyl enzalutamide, and to a lesser extent by CYP450 3A4.', 'DDInter', 1767317475, 'Concomitant use of enzalutamide with potent CYP450 2C8 and/or 3A4 inducers such as carbamazepine, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort should generally be avoided. Moderate inducers such as bosentan, efavirenz, etravirine, modafinil, nafcillin, and nevirapine should also be avoided if possible. The extent to which other, less potent CYP450 3A4 inducers may interact with enzalutamide is unknown. Caution is advised if they are used with enzalutamide.', 'Metabolism', 'Concomitant use of enzalutamide with potent CYP450 2C8 and/or 3A4 inducers such as carbamazepine, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
