INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1354953, 'Etravirine', 'Armodafinil', 'Moderate', 'Coadministration of etravirine with a drug that is both a substrate as well as inducer of CYP450 3A4 may result in decreased plasma concentrations of both drugs. Etravirine itself is also a substrate and inducer of CYP450 3A4. Theoretically, metabolism of etravirine and the coadministered drug may be mutually induced when used in combination.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if etravirine is prescribed in combination with a drug that is an inducer of CYP450 3A4. Close clinical and laboratory monitoring of antiretroviral response is recommended. In addition, dosage adjustments may be required for the coadministered drug if it is also a substrate of CYP450 3A4.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if etravirine is prescribed in combination with a drug that is an inducer of CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
