INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1355071, 'Phenytoin', 'Armodafinil', 'Moderate', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may increase the plasma concentrations of phenytoin. The proposed mechanism is decreased clearance due to inhibition of CYP450 2C19 activity by modafinil and armodafinil. Conversely, phenytoin, a potent inducer of CYP450 3A4, may decrease the plasma levels of modafinil or armodafinil, which are both partially metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if modafinil or armodafinil must be used concurrently with phenytoin. Pharmacologic response to modafinil or armodafinil should be monitored more closely and patients should be monitored for signs of phenytoin toxicity. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever these drugs are added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if modafinil or armodafinil must be used concurrently with phenytoin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
