INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (52755, 'Cabazitaxel', 'Sorafenib', 'Moderate', 'Based on in vitro inhibition data, coadministration with sorafenib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by sorafenib.', NULL, 'Caution is advised when sorafenib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when sorafenib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever sorafenib is added to or withdrawn from therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/52826/', '[1] "Product Information. Nexavar (sorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Nexavar (sorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (52756, 'Cabazitaxel', 'St. John''s Wort', 'Moderate', 'Coadministration with St. John''s wort may alter the pharmacokinetics of antineoplastic agents that are substrates of the CYP450 3A4 isoenzyme (e.g., abiraterone, bexarotene, bortezomib, busulfan, cyclophosphamide, dasatinib, docetaxel, doxorubicin, etoposide, exemestane, ifosfamide, imatinib, irinotecan, letrozole, paclitaxel, sorafenib, sunitinib, tamoxifen, temsirolimus, teniposide, thiotepa, toremifene, tretinoin, vandetanib, vinca alkaloids). The mechanism is induction of CYP450 3A4 metabolism by constituents of St. John''s wort, which is expected to reduce the systemic levels and pharmacologic effects of many of these agents. In contrast, some agents such as cyclophosphamide and ifosfamide are prodrugs that are converted to active metabolites by CYP450 3A4, thus pharmacologic effects may be enhanced by St. John''s wort.', NULL, 'Until more information is available, the use of St.', NULL, 'Until more information is available, the use of St. John''s wort should probably be avoided before and during the entire course of chemotherapy. It is not known how much time should elapse between the discontinuation of St. John''s wort and initiation of chemotherapy. Patients should be advised to consult with their caregivers before using any herbal or alternative medicines.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/52827/', '[1] Sparreboom A, Cox MC, Acharya MR, Figg WD "Herbal remedies in the United States: potential adverse interactions with anticancer agents." J Clin Oncol 22 (2004): 2489-503[2] Roby CA, Anderson GD, Kantor E, Dryer DA, Burstein AH "St John''s Wort: Effect on CYP3A4 activity." Clin Pharmacol Ther 67 (2000): 451-7[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767365204);
