INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (52981, 'Digoxin', 'Cabozantinib', 'Moderate', 'Based on in vitro inhibition data, coadministration with cabozantinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by cabozantinib.', NULL, 'Caution is advised when cabozantinib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when cabozantinib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever cabozantinib is added to or withdrawn from therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53052/', '[1] "Product Information. Cometriq (cabozantinib)." Exelixis Inc, S San Francisco, CA.[2] "Product Information. Cometriq (cabozantinib)." Exelixis Inc, S San Francisco, CA.', 1767365204);
