INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105284, 'Ranitidine (bismuth citrate)', 'Omadacycline', 'Moderate', 'INTERVAL: Administration of a bismuth-containing preparation within two to three hours of a tetracycline may significantly decrease serum tetracycline concentrations. Data are available for tetracycline and doxycycline. The proposed mechanism is chelation of tetracycline by bismuth.', NULL, 'Administration of a tetracycline and bismuth-containing preparation should be separated by two to three hours.', NULL, 'Administration of a tetracycline and bismuth-containing preparation should be separated by two to three hours. Patients should be monitored for diminished tetracycline efficacy.', 'Absorption', 'Tigecycline, Eravacycline', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105403/', '[1] "Product Information. Nuzyra (omadacycline)." Paratek Pharmaceuticals, Inc., Boston, MA.[2] Albert KS, Welch RD, DeSante KA, DiSanto AR "Decreased tetracycline bioavailability caused by a bismuth subsalicylate antidiarrheal mixture." J Pharm Sci 68 (1979): 586-8[3] Ericsson CD, Feldman S, Pickering LK, Cleary TG "Influence of subsalicylate bismuth on absorption of doxycycline." JAMA 247 (1982): 2266-7[4] "Product Information. Seysara (sarecycline)." Allergan Inc, Irvine, CA.[5] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[6] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105285, 'Sucralfate', 'Omadacycline', 'Moderate', 'INTERVAL: Simultaneous administration of sucralfate may interfere with the absorption of tetracyclines and reduce their oral bioavailability. Sucralfate is an aluminum salt of a sulfated disaccharide. The aluminum moiety can dissociate at gastric pH and chelate with tetracyclines to form an insoluble complex that is poorly absorbed from the gastrointestinal tract. Reduced antibiotic efficacy may occur.', NULL, 'To minimize the potential for interaction, tetracyclines should be administered at least 2 hours before sucralfate.', NULL, 'To minimize the potential for interaction, tetracyclines should be administered at least 2 hours before sucralfate.', 'Absorption', 'Tinidazole, Dexlansoprazole, Rabeprazole, Metronidazole, Vonoprazan, Amoxicillin, Pantoprazole, Misoprostol, Esomeprazole, Ranitidine, Levofloxacin, More', 'Tigecycline, Eravacycline', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105404/', '[1] "Product Information. Carafate (sucralfate)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[3] McCarthy DM "Sucralfate." N Engl J Med 325 (1991): 1017-25[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[6] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105286, 'Iron sucrose', 'Omadacycline', 'Moderate', 'INTERVAL: Coadministration with sucroferric oxyhydroxide may reduce the oral bioavailability of tetracyclines. The proposed mechanism is chelation of tetracyclines to the ferric ion component of sucroferric oxyhydroxide, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'Doxycycline should be administered at least 1 hour before sucroferric oxyhydroxide.', NULL, 'Doxycycline should be administered at least 1 hour before sucroferric oxyhydroxide. Omadacycline administration with sucroferric oxyhydroxide should be separated by 4 hours. No dosing recommendations are available for use with other tetracyclines, although patients should consider following the same precaution.', 'Absorption', 'Cyanocobalamin, Folic acid, Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Difelikefalin, Cobicistat, Deferoxamine, More', 'Tigecycline, Eravacycline', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105405/', '[1] Neuvonen PJ "Interactions with the absorption of tetracyclines." Drugs 11 (1976): 45-54[2] "Product Information. Nuzyra (omadacycline)." Paratek Pharmaceuticals, Inc., Boston, MA.[3] "Product Information. Velphoro (sucroferric oxyhydroxide)." Fresenius Medical Care North America, Lexington, MA.[4] Campbell NR, Hasinoff BB "Iron supplements: a common cause of drug interactions." Br J Clin Pharmacol 31 (1991): 251-5[5] Greenberger NJ "Absorption of tetracyclines: interference by iron." Ann Intern Med 74 (1971): 792-3[6] Neuvonen PJ, Gothoni G, Hackman R, Bjorksten K "Interference of iron with the abs