INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105302, 'Paritaprevir', 'Atazanavir', 'Major', 'Coadministration of atazanavir plus ritonavir in combination with ombitasvir/paritaprevir/ritonavir plus dasabuvir may significantly increase the plasma concentrations of paritaprevir. The mechanism may involve inhibition of both CYP450 3A4-mediated metabolism and P-glycoprotein-mediated transport by atazanavir and ritonavir. Coadministration of atazanavir with ombitasvir/paritaprevir/ritonavir plus dasabuvir may increase the risk of hyperbilirubinemia. Both atazanavir and paritaprevir can cause elevations in indirect (unconjugated) bilirubin, the former by inhibition of UGT1A1 and the latter by inhibition of OATP1B1/1B3.', NULL, 'When coadministered with ombitasvir/paritaprevir/ritonavir plus dasabuvir, atazanavir 300 mg should be given in the morning without ritonavir.', NULL, 'When coadministered with ombitasvir/paritaprevir/ritonavir plus dasabuvir, atazanavir 300 mg should be given in the morning without ritonavir. The ritonavir component of the HIV antiretroviral regimen should be restarted after completion of treatment with ombitasvir/paritaprevir/ritonavir plus dasabuvir.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, More', 'Cabotegravir, Amprenavir, Zanamivir, Velpatasvir, Rilpivirine, Oseltamivir, Cobicistat, Remdesivir, Famciclovir, Darunavir, Maribavir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105421/', '[1] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.[2] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[3] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[4] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[5] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105303, 'Bedaquiline', 'Ritonavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of bedaquiline, which is primarily metabolized by CYP450 3A4 to its less active N-monodesmethyl metabolite, M2.', NULL, 'The concomitant use of potent CYP450 3A4 inhibitors such as clarithromycin, cobicistat, conivaptan, delavirdine, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents given systemically for more than 14 consecutive days should generally be avoided during treatment with bedaquiline unless the benefit is anticipated to outweigh the risk.', NULL, 'The concomitant use of potent CYP450 3A4 inhibitors such as clarithromycin, cobicistat, conivaptan, delavirdine, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents given systemically for more than 14 consecutive days should generally be avoided during treatment with bedaquiline unless the benefit is anticipated to outweigh the risk. Appropriate clinical monitoring for bedaquiline-related adverse reactions is recommended if coadministration is required. ECG, serum electrolytes (potassium, magnesium, calcium), and liver function tests (ALT, AST, alkaline phosphatase, bilirubin) should be monitored before starting bedaquiline therapy and periodically during treatment in accordance with the product labeling. Hypokalemia, hypomagnesemia, and hypocalcemia must be corrected prior to bedaquiline administration. Bedaquiline should be interrupted in patients who develop clinically significant ventricular arrhythmia or a QTcF interval greater than 500 msec confirmed by repeat ECG. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Paritaprevir, Glecaprevir, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Capreomycin, Rifamycin, Streptomycin, Cycloserine, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105422/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105304, 'Bempedoic acid', 'Paritaprevir', 'Moderate', 'Coadministration with bempedoic acid may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of the uptake transporters organic anion transporting polypeptide (OATP) 1B1 and 1B3. The proposed mechanism, based on in vitro data, is decreased clearance due to bempedoic acid-mediated inhibition of OATP1B1 and 1B3.', NULL, 'Caution is advised if bempedoic acid is used concomitantly with drugs that are substrates of OATP1B1 or 1B3, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if bempedoic acid is used concomitantly with drugs that are substrates of OATP1B1 or 1B3, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever bempedoic acid is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Lamivudine, Tenofovir alafenamide, Maraviroc, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, More', 'Cholestyramine, Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Probucol, Cerivastatin, Lomitapide, Ezetimibe, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105423/', '[1] "Product Information. Nexletol (bempedoic acid)." Esperion Therapeutics, Ann Arbor, MI.[2] "Product Information. Nexlizet (bempedoic acid-ezetimibe)." Esperion Therapeutics, Ann Arbor, MI.[3] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105305, 'Berotralstat', 'Ritonavir', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may significantly increase the plasma concentrations of berotralstat. Berotralstat is a substrate of both P-gp and BCRP.', NULL, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors.', NULL, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors. Patients should be advised to contact their physician if they experience undue adverse effects of berotralstat such as abdominal pain, vomiting, or diarrhea. Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Absorption', 'Lamivudine, Cabotegravir, Simeprevir, Zanamivir, Valaciclovir, Telaprevir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, More', 'Hemin, Conestat alfa, Hyaluronidase, Streptokinase, Human C1-esterase inhibitor, Crizanlizumab, Lanadelumab, Icatibant', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105424/', '[1] "Product Information. Orladeyo (berotralstat)." BioCryst Pharmaceuticals Inc, Durham, NC.[2] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of quinidine in healthy volunteers." J Clin Pharmacol 36 (1996): 469-76[3] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[4] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[5] Ha HR, Chen J, Leuenberger PM, Freiburghaus AU, Follah F "In vitro inhibition of midazolam and quinidine metabolism by flavonoids." Eur J Clin Pharmacol 48 (1995): 367-71', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105306, 'Boceprevir', 'Ritonavir', 'Moderate', 'Coadministration of boceprevir and some ritonavir-boosted protease inhibitor regimens has been associated with decreased plasma concentrations of both boceprevir and the protease inhibitors. The mechanism of interaction has not been described. Data are currently unavailable regarding a potential interaction between boceprevir and other protease inhibitors, whether alone or ritonavir-boosted.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, caution is advised if boceprevir is used in combination with ritonavir-boosted protease inhibitor regimens.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, caution is advised if boceprevir is used in combination with ritonavir-boosted protease inhibitor regimens. Patients coinfected with chronic HCV and HIV who have been started on one of these combinations should be closely monitored for treatment response and potential HCV and HIV virologic rebound. The safety and efficacy of boceprevir has not been established in the HCV/HIV coinfected population.', 'Others', 'Lamivudine, Glecaprevir, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, More', 'Paritaprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Oseltamivir, Darunavir, Maribavir, Rimantadine, Elvitegravir, Telbivudine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105425/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation, Kenilworth, NJ.[2] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Important drug interactions between (boceprevir) and ritonavir-boosted human immunodeficiency virus (HIV) protease inhibitor drugs. Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm291119.htm." ([2012 Feb 8]):[3] "Product Information. Agenerase (amprenavir)." Glaxo Wellcome, Research Triangle Pk, NC.[4] Demarles D, Gillotin C, Bonaventure-Paci S, Vincent I, Fosse S, Taburet AM "Single-dose pharmacokinetics of amprenavir coadministered with grapefruit juice." Antimicrob Agents Chemother 46 (2002): 1589-1590[5] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation, Kenilworth, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105307, 'Conjugated estrogens', 'Paritaprevir', 'Major', 'Theoretical concerns exist that coadministration of estrogen-containing products may increase the risk of liver enzyme elevations associated with the use of ombitasvir/paritaprevir/ritonavir, with or without dasabuvir. The proposed mechanism is the inhibition of UDP-glucuronosyltransferase (UGT) by ombitasvir, paritaprevir, and dasabuvir.', NULL, 'Caution is recommended if estrogen-containing products are used in patients receiving ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.', NULL, 'Caution is recommended if estrogen-containing products are used in patients receiving ombitasvir/paritaprevir/ritonavir, with or without dasabuvir. Liver function tests should be performed on all patients during the first 4 weeks of treatment and as clinically indicated thereafter. If ALT is elevated above baseline at any time during treatment, the test should be repeated and monitored closely. Therapy should be discontinued if ALT levels remain persistently greater than 10 times the ULN, or if ALT elevations are accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Metabolism', 'Bazedoxifene', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Velpatasvir, Rilpivirine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105427/', '[1] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.[2] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.[4] Schubert W, Eriksson U, Edgar B, Cullberg G, Hedner T "Flavonoids in grapefruit juice inhibit the in vitro hepatic metabolism of 17B-estradiol." Eur J Drug Metab Pharmacokinet 20 (1995): 219-24[5] Weber A, Jager R, Borner A, et al. "Can grapefruit juice influence ethinyl estradiol bioavailability?" Contraception 53 (1996): 41-7', 1767369485);
