INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105332, 'Ritonavir', 'Modafinil', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme. Conversely, the plasma levels of some of these inhibitors may decrease, since many of them are also substrates of CYP450 3A4, and modafinil and armodafinil have been found to be modest inducers of CYP450 3A4. The clinical significance of this potential interaction is unknown. Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.', NULL, '-', NULL, '-', 'Metabolism', 'Lamivudine, Paritaprevir, Cabotegravir, Simeprevir, Zanamivir, Velpatasvir, Oseltamivir, Brincidofovir, Stavudine, Remdesivir, Elbasvir, More', 'Pemoline, Lisdexamfetamine, Solriamfetol, Caffeine, Dexmethylphenidate, Citicoline', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105453/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.[3] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[4] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[5] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105333, 'Ritonavir', 'Nelfinavir', 'Moderate', 'Coadministration with ritonavir may significantly increase the plasma concentrations of nelfinavir. The mechanism is ritonavir inhibition of CYP450 3A4 metabolism of nelfinavir.', NULL, 'Alteration in dosages or regimen is recommended by the manufacturer of nelfinavir during concomitant therapy with ritonavir.', NULL, 'Alteration in dosages or regimen is recommended by the manufacturer of nelfinavir during concomitant therapy with ritonavir. However, appropriate dosages for the combination, with respect to safety and efficacy, have not been established. Patients receiving the combination should be closely monitored for toxicity such as elevations in liver function tests and undue gastrointestinal disturbances, and the dosage(s) adjusted as necessary.', 'Metabolism', 'Paritaprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Oseltamivir, Darunavir, Maribavir, Rimantadine, Elvitegravir, Telbivudine, More', 'Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, Oseltamivir, Brincidofovir, Remdesivir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105454/', '[1] Kempf DJ, Marsh KC, Kumar G, et al "Pharmacokinetic enhancement of inhibitors of the human immunodeficiency virus protease by coadministration with ritonavir." Antimicrob Agents Chemother 41 (1997): 654-60[2] Lu JF, Blaschke TF, Flexner C, Rosenkranz SL, Sheiner LB "Model-based Analysis of the Pharmacokinetic Interactions Between Ritonavir, Nelfinavir, and Saquinavir after Simultaneous and Staggered Oral Administration." Drug Metab Dispos 30 (2002): 1455-61[3] Kempf D, Marsh K, Denissen J, Kumar G, Rodrigues D, McDonald E, Flentge C, Green B, Chen X, Leonard J, Norbeck D "Coadministration with ritonavir enhances the plasma levels of HIV protease inhibitors by inhibition of cytochrome P450." 3rd Conf Retro and Opportun Infect (1996): 79[4] "Product Information. Viracept (nelfinavir)." Agouron Pharma Inc, La Jolla, CA.[5] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.[6] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[7] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105334, 'Paritaprevir', 'Nevirapine', 'Major', 'Coadministration with moderate to potent inducers of CYP450 isoenzymes may significantly decrease the plasma concentrations of ombitasvir, paritaprevir, and dasabuvir. The proposed mechanism may involve induction of CYP450 3A4.', NULL, 'Concomitant use of CYP450 3A4 inducers such etravirine or nevirapine with ombitasvir/paritaprevir/ritonavir with or without dasabuvir is considered contraindicated by some authorities.', NULL, 'Concomitant use of CYP450 3A4 inducers such etravirine or nevirapine with ombitasvir/paritaprevir/ritonavir with or without dasabuvir is considered contraindicated by some authorities.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, More', 'Tenofovir alafenamide, Cabotegravir, Zanamivir, Oseltamivir, Brincidofovir, Famciclovir, Voxilaprevir, Tecovirimat, Pibrentasvir, Rimantadine, Telbivudine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105455/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Department of Health andHuman Services "Guidelines for the Use of Antiretroviral Agents inHIV-1-Infected Adults and Adolescents. Available from: URL: https://aidsinfo.nih.gov/contentfiles/lvguidelines/AdultAndAdolescentGL.pdf." ([2015, April 8]):[4] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[5] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.[6] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[7] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105335, 'Paritaprevir', 'Omeprazole', 'Moderate', 'Coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir may decrease the plasma concentrations of omeprazole. The proposed mechanism of interaction is CYP450 2C19 induction by ritonavir.', NULL, 'Patients should be monitored for potentially diminished efficacy of omeprazole during coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir.', NULL, 'Patients should be monitored for potentially diminished efficacy of omeprazole during coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir. The dosage of omeprazole may be increased in patients whose symptoms are not well controlled; however, some authorities suggest that the dosage of omeprazole should be limited to 40 mg/day.', 'Metabolism', 'Tinidazole, Pantoprazole, Levofloxacin, Dexlansoprazole, Rabeprazole, Sucralfate, Tetracycline, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, More', 'Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Telaprevir, Dolutegravir, Cobicistat, Brincidofovir, Remdesivir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105456/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.[5] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[6] "Product Information. Viekira Pak (dasabuvir/ombita