INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105409, 'Imatinib', 'Rifabutin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of imatinib, which is primarily metabolized by the isoenzyme. Therapeutic failure may occur.', NULL, 'Concomitant use of imatinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of imatinib with potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible. If coadministration with a potent CYP450 3A4 inducer is required, some authorities recommend increasing the dosage of imatinib by at least 50% while carefully monitoring clinical response. Imatinib dosages up to 1200 mg/day (600 mg twice daily) have been given to patients receiving concomitant potent CYP450 3A4 inducers.', 'Metabolism', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Trimethoprim, Cycloserine, Isoniazid, Ethambutol, Pyridoxine, Ethionamide, More', 'Futibatinib, Lenvatinib, Trametinib, Nintedanib, Dacomitinib, Alectinib, Binimetinib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105532/', '[1] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Bolton AE, Peng B, Hubert M, et al. "Effect of rifampicin on the pharmacokinetics of imatinib mesylate (Gleevec, STI571) in healthy subjects." Cancer Chemother Pharmacol 53 (2004): 102-6[4] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[8] Cerner Multum, Inc. "Australian Product Information." O 0[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[10] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105410, 'Indinavir', 'Rifabutin', 'Major', 'Coadministration of indinavir and rifabutin may result in increased plasma concentrations of rifabutin and decreased plasma concentrations of indinavir. The mechanism involves indinavir inhibition of rifabutin metabolism via CYP450 3A4 and, conversely, rifabutin induction of indinavir metabolism via the same isoenzyme.', NULL, 'To minimize the risk of rifabutin toxicity including leukopenia, uveitis, arthralgias and skin discoloration, indinavir labeling recommends that rifabutin be administered at half the standard dosage in patients treated with indinavir.', NULL, 'To minimize the risk of rifabutin toxicity including leukopenia, uveitis, arthralgias and skin discoloration, indinavir labeling recommends that rifabutin be administered at half the standard dosage in patients treated with indinavir. Some experts suggest decreasing rifabutin dose from 300 to 150 mg if given daily but administering the full 300 mg dose during intermittent therapy (i.e., twice- or three-times-weekly directly observed therapy). Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, indinavir labeling and some experts also recommend increasing the dosage of indinavir to 1000 mg three times a day during coadministration with rifabutin.', 'Metabolism', 'Lamivudine, Paritaprevir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Remdesivir, Elbasvir, More', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Cycloserine, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid, Tinidazole, More', 'Major', NULL,