INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105418, 'Itraconazole', 'Rifabutin', 'Major', 'Coadministration with rifamycins may significantly decrease the plasma concentrations of both itraconazole and ketoconazole. The proposed mechanism is accelerated clearance of the azole antifungal agent due to induction of CYP450 3A4-mediated metabolism by rifamycins.', NULL, 'The manufacturers recommend that itraconazole and ketoconazole not be used concomitantly or within 2 weeks of treatment with rifamycins, unless the benefits outweigh the risk of potentially reduced antifungal efficacy.', NULL, 'The manufacturers recommend that itraconazole and ketoconazole not be used concomitantly or within 2 weeks of treatment with rifamycins, unless the benefits outweigh the risk of potentially reduced antifungal efficacy. If coadministration is required, the antifungal activity should be monitored and the azole dosage increased as necessary. With rifabutin, however, consideration should also be given to the potential for increased risk of rifabutin toxicity, particularly when the azole dosage is increased. Therapeutic drug monitoring for rifabutin is therefore advisable, and the dosage adjusted if needed. In addition, a complete blood count should be performed at least weekly and as clinically indicated to monitor for development of neutropenia.', 'Metabolism', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Cycloserine, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid, Tinidazole, More', 'Micafungin, Amphotericin B, Oteseconazole, Flucytosine, Anidulafungin', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105541/', '[1] Brass C, Galgiani JN, 