INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1355303, 'Arsenic trioxide', 'Mecobalamin', 'Moderate', 'Arsenic trioxide can cause QT interval prolongation and complete atrioventricular block. Theoretically, use of arsenic trioxide during intensive vitamin B12 therapy for the treatment of megaloblastic anemia may potentiate the risk of cardiac arrhythmias such as ventricular tachycardia and torsade de pointes because of the hypokalemia that may develop during the early phase of vitamin B12 therapy due to increasing potassium requirements as normal erythropoiesis is established. Hypokalemia is a known risk factor for ventricular arrhythmias associated with QT interval prolongation.', 'DDInter', 1767317475, 'Caution is advised when arsenic trioxide is administered to patients requiring vitamin B12 for severe megaloblastic anemia. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with arsenic trioxide. During therapy, potassium concentrations should be kept above 4 mEq/dL.', 'Synergism', 'Caution is advised when arsenic trioxide is administered to patients requiring vitamin B12 for severe megaloblastic anemia.', NULL, NULL, 'Moderate', 'pharmacodynamic');
