INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53001, 'Emtricitabine', 'Cabozantinib', 'Moderate', 'Coadministration of cabozantinib with multidrug resistance-associated proteins (MRP2) inhibitors may increase exposure and toxicity of cabozantinib. In vitro studies have shown MRP2 inhibitors may increase the plasma concentrations of cabozantinib, a MRP2 substrate.', NULL, 'Caution is advised if cabozantinib is used concomitantly with a MRP2 inhibitor.', NULL, 'Caution is advised if cabozantinib is used concomitantly with a MRP2 inhibitor. Monitor for cabozantinib toxicity, such as gastrointestinal perforations and fistulas, hemorrhage, thrombotic events, hypertension, diarrhea, and palmar-plantar erythrodysesthesia. Dose reductions, interruption or discontinuation of cabozantinib may be necessary if toxicity occurs.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53072/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Cometriq (cabozantinib)." Exelixis Inc, S San Francisco, CA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Cabometyx (cabozantinib)." Exelixis Inc, S San Francisco, CA.[5] "Product Information. Cometriq (cabozantinib)." Exelixis Inc, S San Francisco, CA.', 1767365204);
