INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53302, 'Caffeine', 'Methotrexate', 'Moderate', 'Limited data suggest that consumption of greater than 180 mg/day of caffeine may interfere with the efficacy of methotrexate (MTX) in patients with rheumatoid arthritis. The exact mechanism of interaction is unknown but may be related to the antagonistic effect of caffeine on adenosine receptors, as anti-inflammatory properties of MTX is thought to result from the accumulation of adenosine.', NULL, 'Until further information is available, the potential for interaction should be considered in patients who consume substantial amounts of caffeine and caffeine-containing foods and are prescribed methotrexate for rheumatoid arthritis.', NULL, 'Until further information is available, the potential for interaction should be considered in patients who consume substantial amounts of caffeine and caffeine-containing foods and are prescribed methotrexate for rheumatoid arthritis. It may be appropriate to limit caffeine intake if an interaction is suspected in cases of treatment failure.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53373/', '[1] Nesher G, Mates M, Zevin S "Effect of caffeine consumption on efficacy of methotrexate in rheumatoid arthritis." Arthritis Rheum 48 (2003): 571-572[2] Nesher G, Mates M, Zevin S "Effect of caffeine consumption on efficacy of methotrexate in rheumatoid arthritis." Arthritis Rheum 48 (2003): 571-572[3] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[4] Maish WA, Hampton EM, Whitsett TL, Shepard JD, Lovallo WR "Influence of grapefruit juice on caffeine pharmacokinetics and pharmacodynamics." Pharmacotherapy 16 (1996): 1046-52', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53303, 'Caffeine', 'Ramelteon', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations and pharmacologic effects of ramelteon, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when ramelteon is used with CYP450 1A2 inhibitors.', NULL, 'Caution is advised when ramelteon is used with CYP450 1A2 inhibitors. A reduction in the ramelteon dosage may be necessary in patients who experience excessive sedation or other adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53374/', '[1] "Product Information. Rozerem (ramelteon)." Takeda Pharmaceuticals America, Lincolnshire, IL.[2] "Product Information. Rozerem (ramelteon)." Takeda Pharmaceuticals America, Lincolnshire, IL.', 1767365204);
