INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1356040, 'Pitolisant', 'Morphine', 'Moderate', 'Coadministration with pitolisant may reduce plasma concentrations of substrates of CYP450 2C, P-glycoprotein (P-gp), and uridine diphosphate glucuronosyltransferases (UGTs). Based on in vitro data, pitolisant and its main metabolites may induce CYP450 2C, P-gp, and UGTs.', 'DDInter', 1767317475, 'Caution is recommended if pitolisant is used in combination with substrates of CYP450 2C, P-gp, and/or UGTs. Clinical and laboratory monitoring may be appropriate for some drugs whenever pitolisant is added to or withdrawn from therapy.', 'Metabolism', 'Caution is recommended if pitolisant is used in combination with substrates of CYP450 2C, P-gp, and/or UGTs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1356041, 'Polyethylene glycol (3350 with electrolytes)', 'Morphine', 'Moderate', 'The risk of seizures induced by the use of bowel cleansing preparations may be increased in patients on concomitant medications that can lower the seizure threshold such as selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), systemic steroids, carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, and theophylline. Rare cases of generalized tonic-clonic seizures and/or loss of consciousness in association with low serum osmolality and electrolyte abnormalities (e.g., hyponatremia, hypokalemia, hypocalcemia, hypomagnesemia) have been reported with the use of bowel cleansing products in patients with no prior history of seizures. The condition resolved with correction of fluid and electrolyte abnormalities.', 'DDInter', 1767317475, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold. Bowel cleansing preparations should not be used if these patients have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities. Baseline and postprocedure labs including serum electrolytes, calcium, phosphate, BUN, and creatinine should be considered, particularly in the elderly. Patients should be advised not to exceed the recommended dosage of their bowel cleansing preparation and to drink sufficient quantities of clear fluids before, during, and after the bowel preparation process. Administration of an electrolyte rehydration solution may help attenuate the electrolyte abnormalities and hypovolemia.', 'Antagonism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold.', NULL, NULL, 'Moderate', 'pharmacodynamic');
