INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53575, 'Aluminum hydroxide', 'Calcifediol', 'Moderate', 'In patients with advanced renal impairment or on dialysis, chronic use of aluminum-containing preparations during treatment with vitamin D analogs may result in increased blood levels of aluminum and aluminum toxicity. Patients with renal failure are at risk for toxicity due to impaired clearance of aluminum, and the risk may be further increased by the administration of vitamin D analogs due to increased intestinal absorption of aluminum. Toxicity may manifest as osteodystrophy, osteomalacia, myopathy, anemia, and encephalopathy.', NULL, 'Concomitant use of vitamin D analogs and aluminum-containing preparations should be followed with monitoring for signs and symptoms of aluminum toxicity.', NULL, 'Concomitant use of vitamin D analogs and aluminum-containing preparations should be followed with monitoring for signs and symptoms of aluminum toxicity.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53646/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Moon J, Davison A, Bandy B "Vitamin D and aluminum absorption." CMAJ 147 (1992): 1308, 1313[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53576, 'Calcifediol', 'Bendroflumethiazide', 'Moderate', 'Coadministration of thiazide diuretics with high dosages of calcium and/or vitamin D has been associated with reports of hypercalcemia in some patients. Thiazide diuretics inhibit the renal excretion of calcium and may also enhance responsiveness of bone and renal tubule to parathyroid hormone, thus concurrent use of large amounts of calcium or vitamin D can lead to excessively high plasma levels of calcium. Patients who are particularly susceptible include those with hyperparathyroidism, those being treated for osteoporosis, and those receiving high dosages of vitamin D for hypoparathyroidism. Metabolic alkalosis and the milk-alkali syndrome have been reported during prolonged therapy with thiazide diuretics and calcium.', NULL, 'Patients receiving thiazide diuretic therapy should be cautioned against self-treatment with calcium and vitamin D supplements without first talking to their healthcare provider.', NULL, 'Patients receiving thiazide diuretic therapy should be cautioned against self-treatment with calcium and vitamin D supplements without first talking to their healthcare provider. Serum calcium should be monitored if thiazide diuretics are coadministered with high dosages of calcium and/or vitamin D. Patients should be advised to seek medical attention if they experience signs and symptoms of hypercalcemia such as dizziness, weakness, lethargy, headache, myalgia, anorexia, nausea, vomiting, and seizures.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53647/', '[1] Drinka PJ, Nolten WE "Hazards of treating osteoporosis