INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53622, 'Calcifediol', 'Sevelamer', 'Moderate', 'INTERVAL: Bile acid sequestrants and the phosphate binder, sevelamer, can decrease the absorption of fat-soluble vitamins A, D, E, and K. In non-clinical safety studies, rats administered colesevelam at doses greater than 30-fold the projected human clinical dose developed hemorrhage in association with vitamin K deficiency. In a crossover study involving healthy subjects, coadministration of sevelamer with calcitriol resulted in a significant reduction in bioavailability for calcitriol (calcitriol with sevelamer vs calcitriol alone: AUC 137 pg*h/mL vs 318 pg*h/mL and Cmax 40.1 pg/mL vs 49.7 pg/mL, respectively).', NULL, 'Oral vitamin supplements should be administered at least 4 hours before colesevelam and either 1 hour before or 4 to 6 hours after other bile acid sequestrants and sevelamer.', NULL, 'Oral vitamin supplements should be administered at least 4 hours before colesevelam and either 1 hour before or 4 to 6 hours after other bile acid sequestrants and sevelamer.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53693/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Welchol (colesevelam)" Daiichi Sankyo, Inc., Parsippany, NJ.[4] Peirce D, Hossack S, Poole L, et.al "The effect of sevelamer carbonate and lanthanum carbonate on the pharmacokinetics of oral calcitriol." Nephrol Dial Transplant 26 (2011): 1615-21[5] "Product Information. Rocaltrol (calcitriol)." Roche Laboratories, Nutley, NJ.[6] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[7] "Product Information. Renagel (sevelamer)." Genzyme Corporation, Cambridge, MA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53623, 'Calcifediol', 'Sucralfate', 'Moderate', 'In patients with advanced renal impairment or on dialysis, chronic use of aluminum-containing preparations during treatment with vitamin D analogs may result in increased blood levels of aluminum and aluminum toxicity. Patients with renal failure are at risk for toxicity due to impaired clearance of aluminum, and the risk may be further increased by the administration of vitamin D analogs due to increased intestinal absorption of aluminum. Toxicity may manifest as osteodystrophy, osteomalacia, myopathy, anemia, and encephalopathy.', NULL, 'Concomitant use of vitamin D analogs and aluminum-containing preparations should be followed with monitoring for signs and symptoms of aluminum toxicity.', NULL, 'Concomitant use of vitamin D analogs and aluminum-containing preparations should be followed with monitoring for signs and symptoms of aluminum toxicity.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53694/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Moon J, Davison A, Bandy B "Vitamin D and aluminum absorption." CMAJ 147 (1992): 1308, 1313[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767365204);
