INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53671, 'Etelcalcetide', 'Salmon calcitonin', 'Major', 'Concomitant use of etelcalcetide with other medicines known to lower serum calcium may increase the risk of hypocalcemia. The proposed mechanism is additive reduction in serum calcium levels. Significant decreases in serum calcium may cause paraesthesias, myalgias, muscle spasms, seizures, QT interval prolongation, and ventricular arrhythmia. Etelcalcetide given alone may lead to severe hypocalcemia requiring concomitant treatment with therapies to increase serum calcium levels.', NULL, 'Close monitoring of corrected serum calcium levels is recommended if etelcalcetide is used in combination with other medications known to lower serum calcium, such as denosumab, calcitonin, foscarnet, bisphosphonates, or loop diuretics.', NULL, 'Close monitoring of corrected serum calcium levels is recommended if etelcalcetide is used in combination with other medications known to lower serum calcium, such as denosumab, calcitonin, foscarnet, bisphosphonates, or loop diuretics. It is recommended that the patient''s corrected serum calcium level should be measured within one week of initiation or dose adjustment of etelcalcetide and monitored approximately every 4 weeks. In addition, the manufacturer product labeling should be consulted for management guidelines in the event of a reduction in corrected serum calcium levels below the lower limit of the normal range and/or symptoms of hypocalcemia. Patients should also be advised to contact their health care professional if they experience symptoms of hypocalcemia, such as numbness or tingling in the fingers, toes, or around the mouth, muscle spasms, twitches or cramps, seizures, confusion, and irregular heartbeats.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53742/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Skelid (tilundronate)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53672, 'Salmon calcitonin', 'Lithium carbonate', 'Moderate', 'Calcitonin may decrease serum lithium levels by up to 30%. The mechanism may be related to an increase in lithium elimination or a decrease in lithium absorption caused by calcitonin.', NULL, 'Lithium levels should be measured frequently and dosage adjustments made accordingly if these drugs are given concurrently.', NULL, 'Lithium levels should be measured frequently and dosage adjustments made accordingly if these drugs are given concurrently. Patients should be advised to notify their physician if they experience drowsiness, dizziness, weakness, ataxia, vomiting, diarrhea, thirst, blurry vision, or increased urination.', 'Absorption, Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53743/', '[1] Passiu G, Bocchetta A, Martinelli V, Garau P, Del Zompo M, Mathieu A "Calcitonin decreases lithium plasma levels in man. Preliminary report." Int J Clin Pharm Res 18 (1998): 179-81[2] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53673, 'Aluminum hydroxide', 'Calcitriol', 'Moderate', 'In patients with advanced renal impairment or on dialysis, chronic use of aluminum-containing preparations during treatment with vitamin D analogs may result in increased blood levels of aluminum and aluminum toxicity. Patients with renal failure are at risk for toxicity due to impaired clearance of aluminum, and the risk may be further increased by the administration of vitamin D analogs due to increased intestinal absorption of aluminum. Toxicity may manifest as osteodystrophy, osteomalacia, myopathy, anemia, and encephalopathy.', NULL, 'Concomitant use of vitamin D analogs and aluminum-containing preparations should be followed with monitoring for signs and symptoms of aluminum toxicity.', NULL, 'Concomitant use of vitamin D analogs and aluminum-containing preparations should be followed with monitoring for signs and symptoms of aluminum toxicity.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53744/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Moon J, Davison A, Bandy B "Vitamin D and aluminum absorption." CMAJ 147 (1992): 1308, 1313[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767365204);
