INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53820, 'Calcium acetate', 'Thyroid, porcine', 'Moderate', 'INTERVAL: Concurrent administration of calcium-containing products may decrease the oral bioavailability of levothyroxine by one-third in some patients. Pharmacologic effects of levothyroxine may be reduced. The exact mechanism of interaction is unknown but may involve nonspecific adsorption of levothyroxine to calcium at acidic pH levels, resulting in an insoluble complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'Some experts recommend separating the times of administration of levothyroxine and calcium-containing preparations by at least 4 hours.', NULL, 'Some experts recommend separating the times of administration of levothyroxine and calcium-containing preparations by at least 4 hours. Monitoring of serum TSH levels is recommended. Patients with gastrointestinal or malabsorption disorders may be at a greater risk of developing clinical or subclinical hypothyroidism due to this interaction.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53891/', '[1] Csako G, McGriff NJ, Rotman-Pikielny P, Sarlis NJ, Pucino F "Exaggerated levothyroxine malabsorption due to calcium carbonate supplementation in gastrointestinal disorders." Ann Pharmacother 35 (2001): 1578-83[2] Singh N, Singh PN, Hershman JM "Effect of calcium carbonate on the absorption of levothyroxine." JAMA 283 (2000): 2822-5[3] Neafsey PJ "Levothyroxine and calcium interaction: timing is everything." Home Healthc Nurse 22 (2004): 338-9[4] Schneyer CR "Calcium carbonate and reduction of levothyroxine efficacy." JAMA 279 (1998): 750[5] Wohlt PD, Zh