INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53843, 'Diclofenac', 'Carbamazepine', 'Moderate', 'Coadministration with CYP450 2C9 inducers, such as rifampin, may decrease the plasma concentrations and therapeutic effects of diclofenac. The proposed mechanism is rifampin-mediated induction of CYP450 2C9, the isoenzyme responsible for the metabolic clearance of diclofenac.', NULL, 'The potential for diminished pharmacologic effects of diclofenac should be considered during coadministration with CYP450 2C9 inducers, such as rifampin.', NULL, 'The potential for diminished pharmacologic effects of diclofenac should be considered during coadministration with CYP450 2C9 inducers, such as rifampin. Pharmacologic response to diclofenac should be monitored and the diclofenac dosage adjusted as necessary whenever a CYP450 2C9 inducer is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53914/', '[1] "Product Information. Voltaren (diclofenac)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Kumar JS, Rao Mamidi NVS, Chakrapani T "Rifampicin pretreatment reduces bioavailability of diclofenac sodium." Indian J Pharmacol 27 (1995): 183-5[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365204);
