INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53851, 'Diclofenac', 'Cholestyramine', 'Minor', 'Coadministration of cholestyramine and diclofenac significantly reduces the bioavailability of diclofenac. The clinical significance of this reduction is not clear. Separating the administration of diclofenac and cholestyramine doses may diminish the effects of this interaction.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53922/', '[1] Alballa SR, Elsayed YM, Almeshal MA, Gouda MW "The effects of cholestyramine and colestipol on the absorption of diclofenac in man." Int J Clin Pharmacol Ther 32 (1994): 441-5', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53852, 'Diclofenac', 'Cidofovir', 'Major', 'Coadministration of cidofovir with other nephrotoxic agents may increase the risk of renal impairment due to additive effects on the kidney. Dose-related nephrotoxicity is the major toxicity of cidofovir. Cases of acute renal failure resulting in dialysis and/or contributing to death have occurred with as few as one or two doses of cidofovir.', NULL, 'Concomitant use of cidofovir with other potentially nephrotoxic agents (e.', NULL, 'Concomitant use of cidofovir with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; aminosalicylates; antiviral/antiretroviral agents such as acyclovir, adefovir, foscarnet, ganciclovir, and tenofovir; antineoplastics such as aldesleukin, cisplatin, clofarabine, ifosfamide, streptozocin, and high intravenous dosages of methotrexate; chelating agents such as deferasirox, deferoxamine, edetate disodium, and edetate calcium disodium; immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus; intravascular contrast media; intravenous bisphosphonates; intravenous pentamidine; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents; gallium nitrate; lithium; penicillamine) is considered contraindicated.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53923/', '[1] "Product Information. Vistide (cidofovir)." Gilead Sciences, Foster City, CA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53853, 'Diclofenac', 'Cilostazol', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53924/', '[1] "Product Information. Nevanac (nepafenac ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Product Information. Acular (ketorolac)." Allergan Inc, Irvine, CA.[3] "Product Information. Xibrom (bromfenac ophthalmic)." ISTA Pharmaceuticals, Irvine, CA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53854, 'Diclofenac', 'Cimetidine', 'Minor', 'H2 antagonists may alter the disposition of nonsteroidal anti-inflammatory drugs (NSAIDs), resulting in increased or decreased plasma concentrations. Data are varied, even for the same NSAID. The mechanism may be related to inhibition of metabolism, changes in gastric pH that decrease absorption, and/or reduced urinary elimination. Statistically significant changes have been small and of limited clinical significance. Clinical monitoring of patient response and tolerance is recommended.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53925/', '[1] Said SA, Foda AM "Influence of cimetidine on the pharmacokinetics of piroxicam in rat and man." Arzneimittelforschung 39 (1989): 790-2[2] "Product Information. Daypro (oxaprozin)." Searle, Skokie, IL.[3] Scavone JM, Greenblatt DJ, Matlis R, Harmatz JS "Interaction of oxaprozin with acetaminophen, cimetidine, and ranitidine." Eur J Clin Pharmacol 31 (1986): 371-4', 1767365204);
