INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53863, 'Diclofenac', 'Cyclosporine', 'Moderate', 'The concomitant administration of cyclosporine and diclofenac may double the blood levels of diclofenac. In addition, nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the nephrotoxic effects of cyclosporine, especially if dehydration is present. The exact mechanism is unknown.', NULL, 'Diclofenac doses in the low end of the therapeutic range are recommended during concomitant therapy.', NULL, 'Diclofenac doses in the low end of the therapeutic range are recommended during concomitant therapy. Renal function should be closely monitored in patients receiving cyclosporine and NSAIDs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53934/', '[1] "Product Information. Sandimmune (cyclosporine)." Sandoz Pharmaceuticals Corporation, East Hanover, NJ.[2] Ducharme MP, Warbasse LH, Edwards DJ "Disposition of intravenous and oral cyclosporine after administration with grapefruit juice." Clin Pharmacol Ther 57 (1995): 485-91[3] Min DI, Ku YM, Perry PJ, Ukah FO, Ashton K, Martin MF, Hunsicker LG "Effect of grapefruit juice on cyclosporine pharmacokinetics in renal transplant patients." Transplantation 62 (1996): 123-5[4] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[5] Bailey DG, Arnold JMO, Spence JD "Grapefruit juice and drugs - how significant is the interaction." Clin Pharmacokinet 26 (1994): 91-8[6] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[7] H