INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53882, 'Diflunisal', 'Diclofenac', 'Moderate', 'Concomitant use of more than one nonsteroidal anti-inflammatory drug (NSAID) at a time may increase the potential for serious gastrointestinal toxicity including inflammation, bleeding, ulceration, and perforation of the esophagus, stomach, or intestines. These events can occur at any time during NSAID use, with or without warning symptoms. The risk is dependent on both dosage and duration of therapy.', NULL, 'Concomitant use of more than one NSAID at a time should generally be avoided.', NULL, 'Concomitant use of more than one NSAID at a time should generally be avoided. Patients treated with an NSAID should be advised to take it with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, misoprostol, proton pump inhibitors) may be considered in high risk patients.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53953/', '[1] "Product Information. Duexis (famotidine-ibuprofen)." Horizon Pharma USA Inc, Northbrook, IL.[2] "Product Information. Mobic (meloxicam)" Boehringer-Ingelheim, Ridgefield, CT.[3] "Product Information. Ponstel (mefenamic acid)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[4] "Product Information. Naprosyn (naproxen)." Syntex Laboratories Inc, Palo Alto, CA.[5] "Product Information. VIMOVO (esomeprazole-naproxen)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[6] "Product Information. Caldolor (ibuprofen)." Cumberland Pharmaceuticals Inc, Nashville, TN.[7] "Product Information. Meclofenamate Sodium (meclofenamate)." Mylan Pharmaceuticals Inc, Morgantown, WV.[8] "Product Information. Lodine (etodolac)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[9] "Product Information. Daypro (oxaprozin)." Searle, Skokie, IL.[10] "Product Information. Anaprox (naproxen)." Roche Laboratories, Nutley, NJ.[11] "Product Information. Nalfon (fenoprofen)." Xspire Pharma, Ridgeland, MS.[12] "Product Information. Etodolac ER (etodolac)." Taro Pharmaceuticals U.S.A. Inc, Hawthorne, NY.[13] "Product Information. Cambia (diclofenac)." Kowa Pharmaceuticals America (formerly ProEthic), Montgomery, AL.[14] "Product Information. Ketoprofen ER (ketoprofen)." Mylan Pharmaceuticals Inc, Morgantown, WV.[15] "Product Information. Indocin (indomethacin)." Merck & Co, Inc, West Point, PA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53883, 'Digoxin', 'Diclofenac', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', NULL, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53954/', '[1] Rodin SM, Johnson BF "Pharmacokinetic interactions with digoxin." Clin Pharmacokinet 15 (1988): 227-44[2] Jorgensen HS, Christensen HR, Kampmann JP "Interaction between digoxin and indomethacin or ibuprofen." Br J Clin Pharmacol 31 (1991): 108-10[3] Finch MB, Johnston GD, Kelly JG, McDevitt DG "Pharmacokinetics of digoxin alone and in the presence of indomethacin therapy." Br J Clin Pharmacol 17 (1984): 353-5[4] Marcus FI "Pharmacokinetic interactions between digoxin and other drugs." J Am Coll Cardiol 5 (1985): a82-90[5] Brouwers JRBJ, Desmet PAGM "Pharmacokinetic-pharmacodynamic drug interactions with nonsteroidal anti-inflammatory drugs." Clin Pharmacokinet 27 (1994): 462-85[6] Brown DD, Schmid J, Long RA