INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53906, 'Etravirine', 'Diclofenac', 'Moderate', 'Coadministration with etravirine may increase the plasma concentrations of drugs that are substrates of the CYP450 2C19 and/or 2C9 isoenzymes. The mechanism is decreased clearance due to inhibition of these isoenzymes by etravirine.', NULL, 'Caution is advised if etravirine must be used concomitantly with medications that undergo metabolism by CYP450 2C19 and/or 2C9 , particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if etravirine must be used concomitantly with medications that undergo metabolism by CYP450 2C19 and/or 2C9 , particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever etravirine is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53977/', '[1] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.[2] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[3] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[4] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[5] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[6] "Position Statement: evidence-based nutrition principles and recommendations for the treatment and prevention of diabetes related complications. American Diabetes Association." Diabetes 